DROP DATABASE IF EXISTS game_forum_db;
CREATE DATABASE game_forum_db;
USE game_forum_db;

DROP TABLE IF EXISTS 	`User`;
CREATE TABLE IF NOT EXISTS `User` (
	id 				INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,
	username 		VARCHAR(30) NOT NULL UNIQUE KEY,
    pwd				VARCHAR(800) NOT NULL,
    email			VARCHAR(100) NOT NULL UNIQUE KEY,
    avatar			VARCHAR(500),
    phone			VARCHAR(15) NOT NULL UNIQUE KEY,
    gender			ENUM ("MALE","FEMALE","OTHER") NOT NULL,
    fullname		VARCHAR(50),
    date_of_birth	DATE,
    join_date		DATETIME DEFAULT NOW(),
    `role`			ENUM ("ADMIN","MEMBER") DEFAULT "MEMBER",
    activation		BOOLEAN DEFAULT FALSE
);

DROP TABLE IF EXISTS 	Topic;
CREATE TABLE IF NOT EXISTS Topic (
	id 			INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,
	`name` 		VARCHAR(50) NOT NULL UNIQUE KEY
);

DROP TABLE IF EXISTS 	Post;
CREATE TABLE IF NOT EXISTS Post (
	id 				INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,
	title 			VARCHAR(100) NOT NULL,
    topic_id		INT UNSIGNED,
    user_id			INT UNSIGNED,
    post_cover		VARCHAR(1000),
    post_content	TEXT(16000) NOT NULL,
    post_date		DATETIME DEFAULT NOW(),
	post_status		ENUM ("ALLOWED","DENIED", "WAITING") DEFAULT "WAITING",
    post_pin		ENUM ("FIRST", "SECOND", "THIRD", "FOURTH", "FIFTH") UNIQUE KEY,
    FOREIGN KEY (topic_id) REFERENCES Topic(id) ON DELETE CASCADE,
    FOREIGN KEY (user_id) REFERENCES `User`(id) ON DELETE CASCADE
);

DROP TABLE IF EXISTS 	`Comment`;
CREATE TABLE IF NOT EXISTS `Comment` (
	id 			INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,
    post_id		INT UNSIGNED,
    user_id		INT UNSIGNED,
    cmt_content	VARCHAR(1000) NOT NULL,
    cmt_date	DATETIME DEFAULT NOW(),
    FOREIGN KEY (post_id) REFERENCES Post(id) ON DELETE CASCADE,
    FOREIGN KEY (user_id) REFERENCES `User`(id) ON DELETE CASCADE
);

